import React, { useState, useContext } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useHttpClient } from "../../shared/hooks/http-hook";
import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
} from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";
import "./CourseForm.css";
import ErrorModal from "../../shared/components/UIElements/ErrorModal";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";
import Select from "react-select";

const techCompanies = [
  { label: "Google", value: "Google" },
  { label: "Facebook", value: "Facebook" },
  { label: "Uber", value: "Uber" },
  { label: "Github", value: "Github" },
  { label: "Amazon", value: "Amazon" },
  { label: "Alphabet", value: "Alphabet" },
  { label: "Microsoft", value: "Microsoft" },
  { label: "Apple", value: "Apple" },
  { label: "Twitter", value: "Twitter" },
  { label: "Tesla", value: "Tesla" },
  { label: "SpaceX", value: "SpaceX" },
  { label: "Alibaba", value: "Alibaba" },
  { label: "Nvidia", value: "Nvidia" },
  { label: "Intel", value: "Intel" },
  { label: "Qualcomm", value: "Qualcomm" },
  { label: "Adobe", value: "Adobe" },
  { label: "Amazon", value: "Amazon" },
  { label: "Facebook", value: "Facebook" },
];

const CourseAddParticipants = () => {
  const { isLoading, error, sendRequest, clearError } = useHttpClient();
  const courseID = useParams().courseID;

  const [formState, inputHandler] = useForm(
    {
      moodleID: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const history = useHistory();

  const enrolSubmitHandler = async (event) => {
    event.preventDefault();
    try {
      await sendRequest(
        `http://localhost:5000/api/admin/edit/${courseID}`,
        "PATCH",
        JSON.stringify({
          participants: formState.inputs.moodleID.value,
          //   courseID: courseID,
        }),
        {
          "Content-Type": "application/json",
        }
      );
      history.push("/");
    } catch (error) {}
  };

  return (
    <React.Fragment>
      <ErrorModal error={error} onClear={clearError} />{" "}
      <form className="course-form" onSubmit={enrolSubmitHandler}>
        {" "}
        {isLoading && <LoadingSpinner asOverlay />}{" "}
        <Input
          id="moodleID"
          element="input"
          type="text"
          label="Student Moodle ID"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter a valid moodle id."
          onInput={inputHandler}
        />{" "}
        <div className = "container">
        <div className = "row">
        <div className = "col-md-6">
        <Select
          className="basic-multi-select"
          classNamePrefix="select"
          isMulti
          name="moodleID"
          options={techCompanies}
          onInput={inputHandler}
        />
        </div>
        </div>
        </div>

        <Button type="submit" disabled={!formState.isValid}>
          ADD USER{" "}
        </Button>{" "}
      </form>{" "}
    </React.Fragment>
  );
};

export default CourseAddParticipants;
